import React, { useState } from "react";
import { useNavigate } from "react-router";
import "./Login.scss";

function Login() {
  const [id, setid] = useState("");
  const navigate = useNavigate();
  const gotoList = () => {
    if (
      document.getElementsByClassName("toList")[0].style.backgroundColor ===
      "#79BAF2"
    ) {
      navigate("/List");
    } else {
      document.getElementsByClassName("toList")[0].style.backgroundColor =
        "#b4d2e8";
      alert("asds");
    }
  };

  function handleIdInput(event) {
    setid(event.target.value);
    console.log(id);
    id.includes("@")
      ? (document.getElementsByClassName("id")[0].style.borderColor = "#659c31")
      : (document.getElementsByClassName("id")[0].style.borderColor =
          "#dddddd");
  }

  function handlePwInput(event) {
    let state = event.target.value;
    const num = state.search(/[0-9]/g);
    const eng = state.search(/[a-z]/gi);
    const spe = state.search(/[`~!@@#$%^&*|₩₩₩'₩";:₩/?]/gi);
    state.length > 7 && num > -1 && eng > -1 && spe > -1
      ? (document.getElementsByClassName("pw")[0].style.borderColor = "#659c31")
      : (document.getElementsByClassName("pw")[0].style.borderColor =
          "#dddddd");
  }

  return (
    <div className="login">
      <div className="login-logo">
        <img src="/images/logo.png" alt="로고이미지" />
      </div>
      <form className="login-form">
        <input
          className="id"
          placeholder="전화번호, 사용자 이름 또는 이메일"
          autoComplete="off"
          onChange={(e) => {
            handleIdInput(e);
          }}
        />
        <input
          type="password"
          className="pw"
          placeholder="비밀번호"
          onChange={(e) => {
            handlePwInput(e);
          }}
        />
        <button className="showHide" type="button">
          show
        </button>
        <button className="toList" type="button" onClick={gotoList}>
          로그인
        </button>
      </form>
      <div className="login-forget">
        <a href="#">비밀번호를 잊으셨나요?</a>
      </div>
    </div>
  );
}

export default Login;
