import React, { useState } from "react";
import Nav from "../Component/TopsNav";
import "./Detail.scss";

function Detail(e) {
  //좋아요함수
  let [inputText, setinputText] = useState("");
  const like = (e) => {
    if (e.target.innerHTML === "🤍") {
      e.target.innerHTML = "❤️";
    } else e.target.innerHTML = "🤍";
  };

  return (
    <>
      <Nav />
      {console.log(sessionStorage.getItem("id"))}
      <div class="container">
        <h2>콜드 브루</h2>
        <div class="detail-img">
          <img src="./images/coffee11.png" alt="화이트 초콜릿 모카" />
        </div>
        <div class="detail-info">
          <h3 class="title">화이트 초콜릿 모카</h3>
          <span
            class="like"
            onClick={(e) => {
              like(e);
            }}
          >
            🤍
          </span>
          <p class="smallP">White chocolate Mocha</p>
          <p class="info">
            달콤하고 부드러운 화이트 초콜릿 시럽과 에스프레소를 스팀 밀크와 섞어
            휘핑크림으로 마무리한 음료로 달콤함과 강렬한 에스프레소가 부드럽게
            어우러진 커피
          </p>
          <div class="item">
            <p class="item-info">제품 영양 정보</p>
            <span class="item-span">Tall(톨) / 355ml (12 fl oz)</span>
          </div>
          <div class="detail-kcal">
            <Division />
            <Division />
          </div>
          <div class="알러지">알레르기 유발 요인 : 우유</div>
          <div class="review">
            <h2>리뷰</h2>
            <div class="comment"></div>
            <input
              placeholder="리뷰를 입력해주세요."
              id="commentInput"
              type="text"
              onChange={(e) => {
                setinputText(e.target.value);
              }}
            />
          </div>
        </div>
      </div>

      <BottomBar />
    </>
  );
}

//kcal
function Division() {
  return (
    <div class="division">
      <div class="kcal">
        <p>1회 제공량 (kcal)</p>
        <p>345</p>
      </div>
      <div class="kcal">
        <p>1회 제공량 (kcal)</p>
        <p>345</p>
      </div>
      <div class="kcal">
        <p>1회 제공량 (kcal)</p>
        <p>345</p>
      </div>
    </div>
  );
}
//하단바
function BottomBar() {
  return (
    <div class="bottom-bar">
      <div class="bottom-bar-section">
        <p>COMPANY</p>
        <ul>
          <li>한눈에 보기</li>
          <li>스타벅스 사명</li>
          <li>스타벅스 소개</li>
          <li>국내 뉴스룸</li>
          <li>세계의 스타벅스</li>
          <li>글로벌 뉴스룸</li>
        </ul>
      </div>
      <div class="bottom-bar-section">
        <p>CORPORATE SALES</p>
        <ul>
          <li>단체 및 기업 구매 안내</li>
        </ul>
      </div>
      <div class="bottom-bar-section">
        <p>PARTNERSHIP</p>
        <ul>
          <li>신규 입점 제의</li>
          <li>협력 고객사 등록 신청</li>
        </ul>
      </div>
      <div class="bottom-bar-section">
        <p>ONLINE COMMUNITY</p>
        <ul>
          <li>페이스북</li>
          <li>트위터</li>
          <li>유뷰트</li>
          <li>블로그</li>
          <li>인스타그램</li>
        </ul>
      </div>
      <div class="bottom-bar-section">
        <p>RECRUIT</p>
        <ul>
          <li>채용소개</li>
          <li>채용 지원하기</li>
        </ul>
      </div>
      <div class="bottom-bar-section">
        <p>WEBUCKS</p>
      </div>
    </div>
  );
}
//
function ReviewComment() {
  return (
    <div className="commentBox">
      <span className="commentText">
        sessionStorage.getItem("id")<span className="likeBtn">🤍</span>
      </span>
    </div>
  );
}

export default Detail;
