import React, { useState } from "react";
import { useNavigate } from "react-router";
import "./Login.scss";

function Login() {
  const navigate = useNavigate();
  const gotoList = () => {
    navigate("/List");
  };
  return (
    <div className="login">
      <div className="login-logo">
        <img src="/images/logo.png" alt="로고이미지" />
      </div>
      <form className="login-form">
        <input
          className="id"
          placeholder="전화번호, 사용자 이름 또는 이메일"
          autoComplete="off"
          onChange={(e) => {
            handleIdInput(e);
          }}
        />
        <input
          type="password"
          className="pw"
          placeholder="비밀번호"
          onChange={(e) => {
            handleIdInput(e);
          }}
        />
        <button className="showHide" type="button">
          show
        </button>
        <button className="toList" type="button" onClick={gotoList}>
          로그인
        </button>
      </form>
      <div className="login-forget">
        <a href="#">비밀번호를 잊으셨나요?</a>
      </div>
    </div>
  );
}

function handleIdInput(event) {
  let state = event.target.value;
  const num = state.search(/[0-9]/g);
  const eng = state.search(/[a-z]/gi);
  const spe = state.search(/[`~!@@#$%^&*|₩₩₩'₩";:₩/?]/gi);
  state.includes("@")
    ? (document.getElementsByClassName("id")[0].style.borderColor = "#659c31")
    : (document.getElementsByClassName("id")[0].style.borderColor = "#dddddd");

  state.length > 7 && num > -1 && eng > -1 && spe > -1
    ? (document.getElementsByClassName("id")[0].style.borderColor = "#659c31")
    : (document.getElementsByClassName("id")[0].style.borderColor = "#dddddd");
}

