import React, { useState } from "react";
import { useNavigate } from "react-router";
import "./Login.scss";

function Login() {
  const [id, setid] = useState("");
  const [pw, setipw] = useState("");
  const num = pw.search(/[0-9]/g);
  const eng = pw.search(/[a-z]/gi);
  const spe = pw.search(/[`~!@@#$%^&*|₩₩₩'₩";:₩/?]/gi);
  const navigate = useNavigate();
  const condition =
    id.includes("@") && pw.length > 7 && num > -1 && eng > -1 && spe > -1;

  function handleIdInput(event) {
    const button = document.getElementsByClassName("toList")[0];
    const idDom = document.getElementsByClassName("id")[0];
    setid(event.target.value);
    if (condition) {
      button.style.backgroundColor = "#79BAF2";
    } else {
      button.style.backgroundColor = "#b4d2e8";
    }

    if (id.includes("@")) {
      idDom.style.borderColor = "#659c31";
    } else {
      idDom.style.borderColor = "#dddddd";
    }
  }

  function handlePwInput(event) {
    setipw(event.target.value);
    const button = document.getElementsByClassName("toList")[0];
    const pwDom = document.getElementsByClassName("pw")[0];
    if (condition) {
      button.style.backgroundColor = "#79BAF2";
    } else {
      button.style.backgroundColor = "#b4d2e8";
    }

    if (pw.length > 7 && num > -1 && eng > -1 && spe > -1) {
      pwDom.style.borderColor = "#659c31";
    } else {
      pwDom.style.borderColor = "#dddddd";
    }
  }

  function passwordToText() {
    const pwDom = document.getElementsByClassName("pw")[0];
    if (pwDom.type === "password") {
      pwDom.type = "text";
      pwDom.innerHTML = "hide";
    } else {
      pwDom.type = "password";
      pwDom.innerHTML = "show";
    }
  }

  return (
    <div className="login">
      <div className="login-logo">
        <img src="/images/logo.png" alt="로고이미지" />
      </div>
      <form className="login-form">
        <input
          className="id"
          maxLength="50"
          placeholder="전화번호, 사용자 이름 또는 이메일"
          autoComplete="off"
          onChange={(e) => {
            handleIdInput(e);
          }}
        />
        <input
          type="password"
          className="pw"
          maxLength="50"
          placeholder="비밀번호"
          onChange={(e) => {
            handlePwInput(e);
          }}
        />
        <button
          className="showHide"
          type="button"
          onClick={() => {
            passwordToText();
          }}
        >
          show
        </button>
        <button
          className="toList"
          type="button"
          onClick={() => {
            condition
              ? navigate("/List")
              : alert("이메일과 비밀번호가 올바르지 않습니다.");
          }}
        >
          로그인
        </button>
      </form>
      <div className="login-forget">
        <a href="#">비밀번호를 잊으셨나요?</a>
      </div>
    </div>
  );
}

export default Login;
